import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { addContact, editContact, deleteContact } from "../actions"; // Make sure to import the necessary actions

const Contacts = ({ contacts, addContact, editContact, deleteContact }) => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [status, setStatus] = useState("Active");
  const [showAddForm, setShowAddForm] = useState(false);
  const [selectedContact, setSelectedContact] = useState(null);

  useEffect(() => {
    if (selectedContact) {
      setFirstName(selectedContact.firstName);
      setLastName(selectedContact.lastName);
      setStatus(selectedContact.status);
    } else {
      setFirstName("");
      setLastName("");
      setStatus("Active");
    }
  }, [selectedContact]);

  const handleSubmit = (event) => {
    event.preventDefault();

    if (selectedContact) {
      // Update contact
      const updatedData = {
        firstName,
        lastName,
        status,
      };
      editContact(selectedContact.id, updatedData);
      setSelectedContact(null);
    } else {
      // Add new contact
      const newContact = {
        id: Date.now(),
        firstName,
        lastName,
        status,
      };
      addContact(newContact);
    }

    setFirstName("");
    setLastName("");
    setStatus("Active");
    setShowAddForm(false);
  };

  const handleEdit = (contact) => {
    setSelectedContact(contact);
    setShowAddForm(true);
  };

  const handleDelete = (contactId) => {
    deleteContact(contactId);
  };

  const handleViewDetails = (contact) => {
    setSelectedContact(contact);
    setShowAddForm(false); // Don't show the form when viewing details
  };

  const handleCloseDetails = () => {
    setSelectedContact(null);
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Contacts</h2>
      {showAddForm || selectedContact ? (
        <form onSubmit={handleSubmit} className="mb-4">
          <div className="mb-2">
            <label className="block">First Name:</label>
            <input
              type="text"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
              className="border rounded py-1 px-2 w-full"
              required
            />
          </div>
          <div className="mb-2">
            <label className="block">Last Name:</label>
            <input
              type="text"
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
              className="border rounded py-1 px-2 w-full"
              required
            />
          </div>
          <div className="mb-2">
            <label className="block">Status:</label>
            <label className="mr-4">
              <input
                type="radio"
                value="Active"
                checked={status === "Active"}
                onChange={() => setStatus("Active")}
              />
              Active
            </label>
            <label>
              <input
                type="radio"
                value="Inactive"
                checked={status === "Inactive"}
                onChange={() => setStatus("Inactive")}
              />
              Inactive
            </label>
          </div>
          <button
            type="submit"
            className="bg-blue-500 hover:bg-blue-600 text-white rounded py-1 px-4 mr-2"
          >
            {selectedContact ? "Update Contact" : "Save Contact"}
          </button>
          <button
            onClick={() => setShowAddForm(false)}
            className="bg-gray-300 hover:bg-gray-400 text-gray-700 rounded py-1 px-4"
          >
            Cancel
          </button>
        </form>
      ) : (
        <button
          onClick={() => setShowAddForm(true)}
          className="bg-green-500 hover:bg-green-600 text-white rounded py-1 px-4 mb-4"
        >
          Add Contact
        </button>
      )}
      {contacts.length === 0 ? (
        <h4 className="text-gray-500">No contact found.</h4>
      ) : (
        <ul>
          {contacts.map((contact) => (
            <li key={contact.id} className="mb-4">
              <strong className="text-lg">
                {contact.firstName} {contact.lastName}
              </strong>
              <br />
              Status: {contact.status}
              <div className="mt-2">
                <button
                  onClick={() => handleEdit(contact)}
                  className="bg-blue-500 hover:bg-blue-600 text-white rounded py-1 px-2 mr-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(contact.id)}
                  className="bg-red-500 hover:bg-red-600 text-white rounded py-1 px-2 mr-2"
                >
                  Delete
                </button>
                <button
                  onClick={() => handleViewDetails(contact)}
                  className="bg-gray-300 hover:bg-gray-400 text-gray-700 rounded py-1 px-2"
                >
                  View Details
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
      {selectedContact && (
        <div className="mt-4">
          <h3 className="text-xl font-bold mb-2">Contact Details</h3>
          <p>
            First Name: {selectedContact.firstName}
            <br />
            Last Name: {selectedContact.lastName}
            <br />
            Status: {selectedContact.status}
          </p>
          <button
            onClick={handleCloseDetails}
            className="bg-gray-300 hover:bg-gray-400 text-gray-700 rounded py-1 px-2 mt-2"
          >
            Close
          </button>
        </div>
      )}
    </div>
  );
};

const mapStateToProps = (state) => ({
  contacts: state.contacts,
});

const mapDispatchToProps = {
  addContact,
  editContact,
  deleteContact,
};

export default connect(mapStateToProps, mapDispatchToProps)(Contacts);
