import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import { Line } from "react-chartjs-2";
import "chartjs"; // Import Chart.js
import "moment"; // Import Moment.js for date formatting

const ChartsMaps = () => {
  const [graphData, setGraphData] = useState({});
  const [countriesData, setCountriesData] = useState([]);

  useEffect(() => {
    fetch("https://disease.sh/v3/covid-19/historical/all?lastdays=all")
      .then((response) => response.json())
      .then((data) => {
        const casesData = data.timeline.cases;
        const dates = Object.keys(casesData);
        const cases = Object.values(casesData);

        setGraphData({
          labels: dates,
          datasets: [
            {
              label: "Total Cases",
              data: cases,
              fill: false,
              borderColor: "rgba(75,192,192,1)",
              borderWidth: 2,
            },
          ],
        });
      })
      .catch((error) => console.error(error));

    fetch("https://disease.sh/v3/covid-19/countries")
      .then((response) => response.json())
      .then((data) => setCountriesData(data))
      .catch((error) => console.error(error));
  }, []);

  const options = {
    scales: {
      x: {
        type: "time", // Use the "time" type for the x-axis
        time: {
          unit: "day", // Display data by day
          displayFormats: {
            day: "MMM D", // Format for displaying dates (e.g., "Jan 1")
          },
        },
      },
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div>
      <h2>Worldwide COVID-19 Data</h2>
      <Line data={graphData} options={options} />

      <h2>COVID-19 Cases by Country</h2>
      <MapContainer center={[20, 0]} zoom={2} style={{ height: "400px" }}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        {countriesData.map((country) => (
          <Marker
            key={country.countryInfo._id}
            position={[country.countryInfo.lat, country.countryInfo.long]}
          >
            <Popup>
              <div>
                <h3>{country.country}</h3>
                <p>Total Cases: {country.cases}</p>
                <p>Recovered: {country.recovered}</p>
                <p>Deaths: {country.deaths}</p>
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default ChartsMaps;
