import React, { useState } from "react";
import { connect } from "react-redux";
import { addContact, editContact, deleteContact } from "../actions"; // Make sure to import the necessary actions

const Contacts = ({ contacts, addContact, editContact, deleteContact }) => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [status, setStatus] = useState("Active");
  const [showAddForm, setShowAddForm] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();

    const newContact = {
      id: Date.now(),
      firstName,
      lastName,
      status,
    };
    addContact(newContact);
    setFirstName("");
    setLastName("");
    setStatus("Active");
    setShowAddForm(false);
  };

  const handleEdit = (contactId, updatedData) => {
    editContact(contactId, updatedData);
  };

  const handleDelete = (contactId) => {
    deleteContact(contactId);
  };

  return (
    <div>
      <h2>Contacts</h2>
      {showAddForm ? (
        <form onSubmit={handleSubmit}>
          <div>
            <label>First Name:</label>
            <input
              type="text"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Last Name:</label>
            <input
              type="text"
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Status:</label>
            <label>
              <input
                type="radio"
                value="Active"
                checked={status === "Active"}
                onChange={() => setStatus("Active")}
              />
              Active
            </label>
            <label>
              <input
                type="radio"
                value="Inactive"
                checked={status === "Inactive"}
                onChange={() => setStatus("Inactive")}
              />
              Inactive
            </label>
          </div>
          <button type="submit">Save Contact</button>
          <button onClick={() => setShowAddForm(false)}>Cancel</button>
        </form>
      ) : (
        <button onClick={() => setShowAddForm(true)}>Add Contact</button>
      )}
      <ul>
        {contacts.map((contact) => (
          <li key={contact.id}>
            <strong>
              {contact.firstName} {contact.lastName}
            </strong>
            <br />
            Status: {contact.status}
            <div>
              <button
                onClick={() => handleEdit(contact.id, { status: "Inactive" })}
              >
                Edit
              </button>
              <button onClick={() => handleDelete(contact.id)}>Delete</button>
              <button
                onClick={() =>
                  alert(
                    `Viewing details for ${contact.firstName} ${contact.lastName}`
                  )
                }
              >
                View Details
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

const mapStateToProps = (state) => ({
  contacts: state.contacts,
});

const mapDispatchToProps = {
  addContact,
  editContact,
  deleteContact,
};

export default connect(mapStateToProps, mapDispatchToProps)(Contacts);
