import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { addContact, editContact, deleteContact } from "../actions"; // Make sure to import the necessary actions

const Contacts = ({ contacts, addContact, editContact, deleteContact }) => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [status, setStatus] = useState("Active");
  const [showAddForm, setShowAddForm] = useState(false);
  const [selectedContact, setSelectedContact] = useState(null);

  useEffect(() => {
    if (selectedContact) {
      setFirstName(selectedContact.firstName);
      setLastName(selectedContact.lastName);
      setStatus(selectedContact.status);
    } else {
      setFirstName("");
      setLastName("");
      setStatus("Active");
    }
  }, [selectedContact]);

  const handleSubmit = (event) => {
    event.preventDefault();

    if (selectedContact) {
      // Update contact
      const updatedData = {
        firstName,
        lastName,
        status,
      };
      editContact(selectedContact.id, updatedData);
      setSelectedContact(null);
    } else {
      // Add new contact
      const newContact = {
        id: Date.now(),
        firstName,
        lastName,
        status,
      };
      addContact(newContact);
    }

    setFirstName("");
    setLastName("");
    setStatus("Active");
    setShowAddForm(false);
  };

  const handleEdit = (contact) => {
    setSelectedContact(contact);
    setShowAddForm(true);
  };

  const handleDelete = (contactId) => {
    deleteContact(contactId);
  };

  const handleViewDetails = (contact) => {
    setSelectedContact(contact);
  };

  const handleCloseDetails = () => {
    setSelectedContact(null);
  };

  return (
    <div>
      <h2>Contacts</h2>
      {showAddForm || selectedContact ? (
        <form onSubmit={handleSubmit}>
          <div>
            <label>First Name:</label>
            <input
              type="text"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Last Name:</label>
            <input
              type="text"
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Status:</label>
            <label>
              <input
                type="radio"
                value="Active"
                checked={status === "Active"}
                onChange={() => setStatus("Active")}
              />
              Active
            </label>
            <label>
              <input
                type="radio"
                value="Inactive"
                checked={status === "Inactive"}
                onChange={() => setStatus("Inactive")}
              />
              Inactive
            </label>
          </div>
          <button type="submit">
            {selectedContact ? "Update Contact" : "Save Contact"}
          </button>
          <button onClick={() => setShowAddForm(false)}>Cancel</button>
        </form>
      ) : (
        <button onClick={() => setShowAddForm(true)}>Add Contact</button>
      )}
      {contacts.length === 0 ? (
        <h4>No contact found.</h4>
      ) : (
        <ul>
          {contacts.map((contact) => (
            <li key={contact.id}>
              <strong>
                {contact.firstName} {contact.lastName}
              </strong>
              <br />
              Status: {contact.status}
              <div>
                <button onClick={() => handleEdit(contact)}>Edit</button>
                <button onClick={() => handleDelete(contact.id)}>Delete</button>
                <button onClick={() => handleViewDetails(contact)}>
                  View Details
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
      {selectedContact && (
        <div>
          <h3>Contact Details</h3>
          <p>
            First Name: {selectedContact.firstName}
            <br />
            Last Name: {selectedContact.lastName}
            <br />
            Status: {selectedContact.status}
          </p>
          <button onClick={handleCloseDetails}>Close</button>
        </div>
      )}
    </div>
  );
};

const mapStateToProps = (state) => ({
  contacts: state.contacts,
});

const mapDispatchToProps = {
  addContact,
  editContact,
  deleteContact,
};

export default connect(mapStateToProps, mapDispatchToProps)(Contacts);
