import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import { Line } from "react-chartjs-2";

const ChartsMaps = () => {
  const [worldData, setWorldData] = useState({});
  const [countriesData, setCountriesData] = useState([]);
  const [graphData, setGraphData] = useState({});

  useEffect(() => {
    // Fetch world data
    fetch("https://disease.sh/v3/covid-19/all")
      .then((response) => response.json())
      .then((data) => setWorldData(data))
      .catch((error) => console.error(error));

    // Fetch country data
    fetch("https://disease.sh/v3/covid-19/countries")
      .then((response) => response.json())
      .then((data) => setCountriesData(data))
      .catch((error) => console.error(error));

    // Fetch graph data
    fetch("https://disease.sh/v3/covid-19/historical/all?lastdays=all")
      .then((response) => response.json())
      .then((data) => {
        // Process data for chart.js
        const dates = Object.keys(data.cases);
        const cases = Object.values(data.cases);
        const deaths = Object.values(data.deaths);
        const recovered = Object.values(data.recovered);

        setGraphData({
          labels: dates,
          datasets: [
            {
              label: "Cases",
              data: cases,
              borderColor: "#ff8c00",
              fill: false,
            },
            {
              label: "Deaths",
              data: deaths,
              borderColor: "#ff0000",
              fill: false,
            },
            {
              label: "Recovered",
              data: recovered,
              borderColor: "#00ff00",
              fill: false,
            },
          ],
        });
      })
      .catch((error) => console.error(error));
  }, []);

  return (
    <div className="flex flex-col items-center p-8">
      <h2 className="text-3xl font-semibold mb-6">COVID-19 Dashboard</h2>

      {/* Line Graph */}
      <div className="w-full max-w-xl">
        <Line data={graphData} options={{ maintainAspectRatio: false }} />
      </div>

      {/* Map */}
      <div className="w-full mt-8">
        <MapContainer center={[0, 0]} zoom={2} style={{ height: "500px" }}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />

          {countriesData.map((country) => (
            <Marker
              key={country.countryInfo._id}
              position={[country.countryInfo.lat, country.countryInfo.long]}
            >
              <Popup>
                <div>
                  <h3>{country.country}</h3>
                  <p>Total Cases: {country.cases}</p>
                  <p>Active Cases: {country.active}</p>
                  <p>Recovered: {country.recovered}</p>
                  <p>Deaths: {country.deaths}</p>
                </div>
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>
    </div>
  );
};

export default ChartsMaps;
